import { GET_ALL_LEAGUES_INFORMATION,ADD_LEAGUE_TO_FAVOURITE } from "../constants/actionTypes";

export type LeagueType = {
    abbr: string;
    id: string;
    logos: { light: string; dark: string };
    name: string;
    slug: string;
};
export interface GeneralStateType {
    leagues: LeagueType[];
    favouriteLeagues: LeagueType[];
}

export const initialState: GeneralStateType = {
    leagues: [],
    favouriteLeagues: [],
};

export default (
    state = initialState,
    action: {
        type: string;
        payload: any;
    }
) => {
    switch (action.type) {
        case GET_ALL_LEAGUES_INFORMATION:
            return {
                ...state,
                leagues: action.payload.data,
            };
            case ADD_LEAGUE_TO_FAVOURITE:
              return {
                  ...state,
                  favouriteLeagues: [...state.favouriteLeagues, state.favouriteLeagues.filter(e=> e.id === action)]
              };
        default:
            return state;
    }
};
