import React, { useCallback } from "react";
import { View, StyleSheet, TouchableOpacity, Image } from "react-native";
import { AntDesign, Ionicons } from "@expo/vector-icons";
import colors from "../constants/colors";
import { useDispatch } from "react-redux";
import { ADD_LEAGUE_TO_FAVOURITE } from "../store/constants/actionTypes";
import { LeagueType } from "../store/reducers/general";
import { Text } from "./Text";

const styles = StyleSheet.create({
    row: {
        flex: 1,
        flexDirection: "row",
        alignItems: "center",
        paddingHorizontal: 15,
        paddingVertical: 10,
        backgroundColor: colors.white,
    },
    titleText: {
        fontWeight: "bold",
    },
    separator: {
        height: StyleSheet.hairlineWidth,
        backgroundColor: colors.border,
    },
    image: {
        height: 50,
        width: 50,
        marginRight: 15,
    },
    wrapper: {
        minWidth: 300,
    },
});

type ListItemProps = {
    item: LeagueType;
    onPress: () => void;
};

export const ListItem = ({ item, onPress = () => null }: ListItemProps) => {
    console.log(item);
    const { logos, name, abbr } = item;
    const dispatch = useDispatch();
    const rowStyles = [styles.row];

    const addToFavourites = useCallback((item) => {
        dispatch({ type: ADD_LEAGUE_TO_FAVOURITE, payload: item });
    }, []);

    return (
        <TouchableOpacity onPress={onPress}>
            <View style={rowStyles}>
                <Image style={[styles.image]} source={{ uri: logos.light }} />
                <View style={[styles.wrapper]}>
                    <Text style={[styles.titleText]}>{name}</Text>
                    <Text>{abbr}</Text>
                </View>
                <Ionicons
                    // onPress={() => addToFavourites(item)}
                    name="heart-outline"
                    size={24}
                    color="#606060"
                />
            </View>
        </TouchableOpacity>
    );
};

export const ListSeparator = () => <View style={styles.separator} />;
