import React, { useEffect, useCallback } from "react";
import { createStackNavigator } from "@react-navigation/stack";
import { useSelector, useDispatch } from "react-redux";
import axios from "axios";
import { List } from "../screens/List";
import { TextDemo } from "../screens/Demos";
import { LeagueType } from "../store/reducers/general";
import { GET_ALL_LEAGUES_INFORMATION } from "../store/constants/actionTypes";
import { Pressable, Text, StyleSheet } from "react-native";

const styles = StyleSheet.create({
    button: {
        alignItems: "center",
        justifyContent: "center",
        paddingVertical: 12,
        paddingHorizontal: 32,
        borderRadius: 4,
        elevation: 3,
        backgroundColor: "",
        marginLeft: 15,
    },
    text: {
        fontSize: 16,
        lineHeight: 21,
        fontWeight: "bold",
        letterSpacing: 0.25,
        color: "white",
    },
});
export const api = axios.create();

export type MainStackParams = {
    Leagues: undefined;
    TextDemo: undefined;
};

const MainStack = createStackNavigator<MainStackParams>();

export const Main = () => {
    const { leagues } = useSelector((state: any) => state.general);
    const dispatch = useDispatch();

    const fetchLeagueInformation = useCallback(async () => {
        api.get("https://api-football-standings.azharimm.site/leagues").then(
            function (response) {
                saveData(response.data);
            }
        );
    }, []);

    const saveData = (data: LeagueType[]) =>
        dispatch({ type: GET_ALL_LEAGUES_INFORMATION, payload: data });

    useEffect(() => {
        fetchLeagueInformation();
    }, [fetchLeagueInformation]);

    return (
        <MainStack.Navigator
            screenOptions={{
                headerLeft: () => (
                    <Pressable style={styles.button} onPress={() => {}}>
                        <Text style={styles.text}>{"Show favourites"}</Text>
                    </Pressable>
                ),
            }}
        >
            <MainStack.Screen
                options={{ title: "Welcome" }}
                name="Leagues"
                component={List}
            />
            {leagues.map((e: LeagueType) => {
                return (
                    <MainStack.Screen
                        name={e.slug as keyof MainStackParams}
                        component={TextDemo}
                        options={{ headerTitle: e.name }}
                    />
                );
            })}
        </MainStack.Navigator>
    );
};
