import React, { useMemo, useState, useCallback } from "react";
import {
    StyleSheet,
    FlatList,
    TouchableHighlight,
    Text,
    TextInput,
} from "react-native";
import { StackNavigationProp } from "@react-navigation/stack";
import { useSelector, useDispatch } from "react-redux";
import colors from "../constants/colors";
import { ListItem, ListSeparator } from "../components/List";
import { MainStackParams } from "../navigation/Main";
import { LeagueType } from "../store/reducers/general";
import {
    SHOW_FAVOURITES,
    SHOW_ALL,
    SET_LEAGUES,
} from "../store/constants/actionTypes";

type Props = {
    navigation: StackNavigationProp<MainStackParams, "Leagues">;
};

export const List = ({ navigation }: Props) => {
    const dispatch = useDispatch();
    const { leagues } = useSelector((state: any) => state.general);

    const [isEnabled, setIsEnabled] = useState<boolean>(false);
    const [filterValue, onFilterChange] = useState<string>("");

    const toggleSwitch = () => {
        isEnabled
            ? dispatch({ type: SHOW_FAVOURITES })
            : dispatch({ type: SHOW_ALL });
        setIsEnabled((previousState) => !previousState);
    };
    const onChangeText = useCallback((text: string) => {
        onFilterChange(text);
        const leaguesCopy = [...leagues].filter(
            (e: LeagueType) => e.name.includes(text) || e.abbr.includes(text)
        );
        dispatch({ type: SET_LEAGUES, payload: text.length > leaguesCopy });
    }, []);

    const screens = useMemo(
        () =>
            leagues.map((e: LeagueType) => {
                return {
                    title: e.name,
                    subtitle: e.abbr,
                    target: e.slug,
                    image: e.logos,
                    id: e.id,
                };
            }),
        [leagues]
    );

    return (
        <FlatList
            style={styles.container}
            data={screens}
            keyExtractor={(item) => item.title}
            renderItem={({ item }) => (
                <ListItem
                    title={item.title}
                    subtitle={item.subtitle}
                    image={item.image}
                    onPress={() => navigation.navigate(item.target)}
                    id={item.id}
                />
            )}
            ItemSeparatorComponent={ListSeparator}
            ListHeaderComponent={
                <TouchableHighlight
                    activeOpacity={0.6}
                    underlayColor="#800080"
                    style={[
                        styles.button,
                        isEnabled ? styles.enabled : styles.disabled,
                    ]}
                    onPress={toggleSwitch}
                >
                    <Text
                        style={[
                            styles.text,
                            isEnabled
                                ? styles.enabledText
                                : styles.disabledText,
                        ]}
                    >
                        {"Show favourites"}
                    </Text>
                    <TextInput
                        style={styles.input}
                        onChangeText={onChangeText}
                        value={filterValue}
                    />
                </TouchableHighlight>
            }
            ListFooterComponent={ListSeparator}
        />
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: colors.background,
        paddingVertical: 20,
    },
    button: {
        alignItems: "center",
        justifyContent: "center",
        paddingVertical: 5,
        paddingHorizontal: 10,
        borderRadius: 20,
        elevation: 3,
        border: "2px solid #800080",
        marginLeft: 15,
        width: 200,
        marginBottom: 15,
    },
    text: {
        fontSize: 16,
        lineHeight: 21,
        fontWeight: "bold",
        letterSpacing: 0.25,
    },
    enabled: {
        backgroundColor: "#800080",
    },
    disabled: {
        backgroundColor: "transparent",
    },
    enabledText: {
        color: "white",
    },
    disabledText: {
        color: "black",
    },
    input: {
        height: 40,
        margin: 12,
        borderWidth: 1,
        padding: 10,
    },
});
