import React, { useEffect, useCallback, useState } from "react";
import { createStackNavigator } from "@react-navigation/stack";
import { useSelector, useDispatch } from "react-redux";
import axios from "axios";
import { List } from "../screens/List";
import { TextDemo } from "../screens/Demos";
import { LeagueType } from "../store/reducers/general";
import {
    GET_ALL_LEAGUES_INFORMATION,
    SHOW_FAVOURITES,
    SHOW_ALL,
} from "../store/constants/actionTypes";
import { TouchableHighlight, Text, StyleSheet } from "react-native";

const styles = StyleSheet.create({
    button: {
        alignItems: "center",
        justifyContent: "center",
        paddingVertical: 5,
        paddingHorizontal: 10,
        borderRadius: 20,
        elevation: 3,
        border: "2px solid #800080",
        marginLeft: 15,
    },
    text: {
        fontSize: 16,
        lineHeight: 21,
        fontWeight: "bold",
        letterSpacing: 0.25,
    },
    enabled: {
        backgroundColor: "#800080",
    },
    disabled: {
        backgroundColor: "transparent",
    },
    enabledText: {
        color: "white",
    },
    disabledText: {
        color: "black",
    },
});
export const api = axios.create();

export type MainStackParams = {
    Leagues: undefined;
    TextDemo: undefined;
};

const MainStack = createStackNavigator<MainStackParams>();

export const Main = () => {
    const dispatch = useDispatch();
    const [isEnabled, setIsEnabled] = useState(false);
    const toggleSwitch = () => {
        isEnabled
            ? dispatch({ type: SHOW_FAVOURITES })
            : dispatch({ type: SHOW_ALL });
        setIsEnabled((previousState) => !previousState);
    };

    const { leagues } = useSelector((state: any) => state.general);

    const fetchLeagueInformation = useCallback(async () => {
        api.get("https://api-football-standings.azharimm.site/leagues").then(
            function (response) {
                saveData(response.data);
            }
        );
    }, []);

    const saveData = (data: LeagueType[]) =>
        dispatch({ type: GET_ALL_LEAGUES_INFORMATION, payload: data });

    useEffect(() => {
        fetchLeagueInformation();
    }, [fetchLeagueInformation]);

    return (
        <MainStack.Navigator
            screenOptions={{
                headerLeft: () => (
                    <TouchableHighlight
                        activeOpacity={0.6}
                        underlayColor="#800080"
                        style={[
                            styles.button,
                            isEnabled ? styles.enabled : styles.disabled,
                        ]}
                        onPress={toggleSwitch}
                    >
                        <Text
                            style={[
                                styles.text,
                                isEnabled
                                    ? styles.enabledText
                                    : styles.disabledText,
                            ]}
                        >
                            {"Show favourites"}
                        </Text>
                    </TouchableHighlight>
                ),
            }}
        >
            <MainStack.Screen
                options={{ title: "Leagues" }}
                name="Leagues"
                component={List}
            />
            {leagues.map((e: LeagueType) => {
                return (
                    <MainStack.Screen
                        name={e.slug as keyof MainStackParams}
                        component={TextDemo}
                        options={{ headerTitle: e.name }}
                    />
                );
            })}
        </MainStack.Navigator>
    );
};
